import React, {
  useState,
  useContext,
  createContext,
  useReducer,
} from 'react';
import {
  BrowserRouter as Router,
  Route,
  Link,
  Switch,
} from 'react-router-dom';
import {
  ActionProvider,
  StateContext,
  DispatchContext,
} from './action-context';
import Modal, { HandleShowModal } from './utils/Modal/Modal';
import Listing from './components/Listing/Listing';
import Home from './components/Home/Home';
import './App.css';
import './config';

export const App = () => {
  const {
    HandleModalOverlayClick,
    ShowModal,
    showModal,
    type,
  } = HandleShowModal();

  return (
    <ActionProvider>
      <>
        <Router>
          <Link id="HomeLink" to={'/'}>
            Home
          </Link>
          |
          <Link id="BooksLink" to={'/books'}>
            Books
          </Link>
          |
          <Link id="AuthorsLink" to={'/authors'}>
            Authors
          </Link>
          <div>
            <button
              id={'CreateBook'}
              onClick={e => ShowModal(e, 'CREATE_BOOK')}
            >
              Create Book
            </button>
            <button
              id={'CreateAuthor'}
              onClick={e => ShowModal(e, 'CREATE_AUTHOR')}
            >
              Create Author
            </button>
          </div>
          <div className="App">
            <Switch>
              <Route
                path="/"
                exact
                render={props => <Home {...props} />}
              />
              <Route
                path="/books"
                exact
                render={props => (
                  <Listing {...props} listingType={'books'} />
                )}
              />
              <Route
                path="/authors"
                exact
                render={props => (
                  <Listing {...props} listingType={'authors'} />
                )}
              />
              <Route path="/nowhere" exact render={() => 'NOWHERE'} />
            </Switch>
          </div>
        </Router>
        {showModal && (
          <Modal
            show={showModal}
            type={type}
            HandleModalOverlayClick={HandleModalOverlayClick}
          />
        )}
      </>
    </ActionProvider>
  );
};

