export default App;

import React, { useEffect } from 'react';
import Modal, { HandleShowModal } from './utils/Modal/Modal';

const StateContext = React.createContext();
const DispatchContext = React.createContext();

const ActionReducer = (state, action) => {
  const { data, listing, type, payload } = action;
  switch (type) {
    case 'ShowModal':
      return { data, payload, listing, type };
    default:
      return state;
  }
};

function ActionProvider({ children }) {
  const [state, dispatch] = React.useReducer(ActionReducer, {
    action: '1',
  });
  const {
    HandleModalOverlayClick,
    ShowModal,
    showModal,
  } = HandleShowModal();

  useEffect(() => {
    if (state?.type === 'ShowModal') {
      ShowModal();
    }
  }, [state]);

  return (
    <>
      <DispatchContext.Provider value={dispatch}>
        <StateContext.Provider value={state}>
          {children}
        </StateContext.Provider>
      </DispatchContext.Provider>
      {state?.type === 'ShowModal' && (
        <Modal
          show={showModal}
          type={state?.listing}
          data={state?.data}
          HandleModalOverlayClick={HandleModalOverlayClick}
        />
      )}
    </>
  );
}
export { ActionProvider, StateContext, DispatchContext };

import React, { useState } from 'react';
import { AuthorSingleItem } from '../../components/Author/AuthorListing';
import CreateAuthor from '../../components/Author/CreateAuthor';
import { BookSingleItem } from '../../components/Book/BookListing';
import CreateBook from '../../components/Book/CreateBook';
import Transition from '../Transition';
import { Modal, ModalOverlay } from './Modal.styled.js';

export const HandleShowModal = () => {
  const [showModal, setShowModal] = useState(false);
  const [type, setType] = useState(null);

  const ShowModal = (e, ModalType) => {
    setShowModal(!showModal);
    setType(ModalType);
  };

  const HandleModalOverlayClick = e => {
    if (e.target === e.currentTarget) {
      setShowModal(false);
    }
  };

  return { HandleModalOverlayClick, ShowModal, showModal, type };
};

const ModalComponent = ({
  show,
  type,
  HandleModalOverlayClick,
  data,
}) => {
  return (
    <ModalOverlay show={show} onClick={HandleModalOverlayClick}>
      <Transition show={show}>
        <Modal>
          {/* Single Book/Author & Edit / Delete */}
          {type === 'BOOK' && BookSingleItem(data)}
          {type === 'AUTHOR' && AuthorSingleItem(data)}

          {/* Create New Book/Author */}
          {type === 'CREATE_BOOK' && <CreateBook />}
          {type === 'CREATE_AUTHOR' && <CreateAuthor />}
        </Modal>
      </Transition>
    </ModalOverlay>
  );
};

export default ModalComponent;

import React, { useContext } from 'react';
