export const AuthorListing = (singleAuthor, dispatch) => {
  return (
    <ListingCard
      key={singleAuthor?._id}
      onClick={() =>
        dispatch({
          data: singleAuthor,
          listing: 'AUTHOR',
          type: 'ShowModal',
          payload: `${singleAuthor?._id}`,
        })
      }
    >
      {AuthorFragment(singleAuthor)}
