};

export const AuthorSingleItem = data => {
  const [
    FullData,
    isLoaded,
    error,
    fetchMore,
    showLoadMore,
  ] = useFetch(`/authors/${data._id}`);
  return (
    <div>
      <div>
        <button>DELETE</button> | <button>EDIT</button>
      </div>
      {isLoaded && FullData._id ? (
        <>
          <p>Author: {FullData?.first_name}</p>
          <p>First Name: {FullData?.name}</p>
          <p>Last Name: {FullData?.last_name}</p>
          <br />
          <b>BOOKS:</b>
          <div>
            {FullData?.books?.map(book => {
              return (
                <div key={book?._id}>
                  <p>{book?._id}</p>
                  <p>{book?.name}</p>
                  <p>{book?.isbn}</p>
                </div>
              );
            })}
          </div>
        </>
      ) : (
        <div>Loading...</div>
      )}
