import { useState, useEffect } from 'react';

const useFetch = url => {
  const [data, setData] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);

  const [theLimit, setTheLimit] = useState(5);
  const [theOffset, setTheOffset] = useState(0);

  const [showLoadMore, setShowLoadMore] = useState(true);

  const [theUrl, setTheUrl] = useState(url);

  function fetchMore() {
    setTheOffset(theOffset => theOffset + theLimit);
  }

  async function fetchUrl() {
    try {
      const response = await fetch(
        `${theUrl}?limit=${theLimit}&offset=${theOffset}`,
      );
      const result = await response.json();
      if (result) {
        if (!result.length) {
          setShowLoadMore(false);
        }
        result && setData(result);
      }
      setIsLoaded(true);
    } catch (error) {
      setError(error);
      setIsLoaded(true);
    }
  }

  useEffect(() => {
    if (theOffset > 0) {
      fetchUrl();
    }
  }, [theOffset]);

  useEffect(() => {
    setTheUrl(url);
    setTheOffset(0);
    setShowLoadMore(true);
  }, [url]);

  useEffect(() => {
    fetchUrl();
    return () => {
      setTheLimit(1);
      setTheOffset(0);
    };
  }, [theUrl]);

  return [data, isLoaded, error, fetchMore, showLoadMore];
};

export { useFetch };

import React, { useState } from 'react';

const CreateAuthor = () => {
  const [firstName, setFirstName] = useState(null);
  const [lastName, setLastName] = useState(null);

  function HandlePost(first_name, last_name) {
    fetch('/author/', {
      method: 'post',
      headers: {
        Accept: 'application/json, text/plain, */*',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(first_name, last_name),
    }).then(res => res.json());
  }

  return (
    <>
      <form
        noValidate
        autoComplete="off"
        encType={'multipart/form-data'}
        onSubmit={e => {
          e.preventDefault();
          HandlePost({
            first_name: firstName,
            last_name: lastName,
          });
        }}
      >
        <input
          type="text"
          placeholder="Author First Name"
          onChange={e => setFirstName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Author Last Name"
          onChange={e => setLastName(e.target.value)}
        />

        <button type="submit" value="Submit">
          Save Author
        </button>
      </form>
    </>
  );
};

export default CreateAuthor;

