import React, { useState } from 'react';

const CreateBook = () => {
  const [bookTitle, setBookTitle] = useState(null);
  const [bookISBN, setBookISBN] = useState(null);
  const [bookAuthor_FirstName, setBookAuthor_FirstName] = useState(null);
  const [bookAuthor_LastName, setBookAuthor_LastName] = useState(null);

  function HandlePost(name, isbn, author) {
    fetch('/books/', {
      method: 'post',
      headers: {
        Accept: 'application/json, text/plain, */*',
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(name, isbn, author),
    }).then(res => res.json());
  }

  return (
    <form
      noValidate
      autoComplete="off"
      encType={'multipart/form-data'}
      onSubmit={e => {
        e.preventDefault();
        HandlePost({
          name: bookTitle,
          isbn: bookISBN,
          author_first_name: bookAuthor_FirstName,
          author_last_name: bookAuthor_LastName,
        });
      }}
    >
      <input
        type="text"
        placeholder="Book Title"
        onChange={e => setBookTitle(e.target.value)}
      />
      <input
        type="text"
        placeholder="Book ISBN"
        onChange={e => setBookISBN(e.target.value)}
      />
      <input
        type="text"
        placeholder="Author First Name"
        onChange={e => setBookAuthor_FirstName(e.target.value)}
      />
      <input
        type="text"
        placeholder="Author Last Name"
        onChange={e => setBookAuthor_LastName(e.target.value)}
      />
      <button type="submit" value="Submit">
        Save Book
      </button>
    </form>
  );
};

export default CreateBook;

import React, { useEffect, useState } from 'react';
import styled, { css } from 'styled-components/macro';

const FadeContainer = styled.div`
  ${props =>
    props.show &&
    css`
      animation: fadeIn 0.3s;
    `}

  ${props =>
    !props.show &&
    css`
      animation: fadeOut 0.3s;
    `}


  @keyframes fadeIn {
    0% {
      opacity: 0;
      transform: translate(0, -20px);
    }
    100% {
      opacity: 1;
    }
  }

  @keyframes fadeOut {
    0% {
      opacity: 1;
    }
    100% {
      opacity: 0;
      transform: translate(0, -20px);
    }
  }
`;

const TransitionFade = ({ show, children }) => {
  const [render, setRender] = useState(show);

  useEffect(() => {
    if (show) setRender(true);
  }, [show]);

  const onAnimationEnd = () => {
    if (!show) setRender(false);
  };

  return (
    render && (
      <FadeContainer show={show} onAnimationEnd={onAnimationEnd}>
        {children}
      </FadeContainer>
    )
  );
};

export default TransitionFade;

import React, { useState, useEffect, useContext } from 'react';
import { useFetch } from '../../utils/FetchHook';
import { BookListing } from '../Book/BookListing';
import { AuthorListing } from '../Author/AuthorListing';
import { ListingContainer } from './Listing.styled';
import { useFetchListAndMerge } from '../../utils/FetchListAndMergeHook';
import {
  ActionProvider,
  StateContext,
  DispatchContext,
} from '../../action-context';

export const List = ({
  data,
  isLoaded,
  error,
  listingType,
  dispatch,
}) => {
  if (!isLoaded) return `Loading...`;
  if (error) return `Error! ${error.message}`;
  if (!data) return <div>There isn't any data here!</div>;
  return (
    <ListingContainer>
      {listingType === 'books' &&
        isLoaded &&
        data?.map(singleListing =>
          BookListing(singleListing, dispatch),
        )}
      {listingType === 'authors' &&
        isLoaded &&
        data?.map(singleListing =>
          AuthorListing(singleListing, dispatch),
        )}
    </ListingContainer>
  );
};

export const Listing = ({ listingType }) => {
  const [allData, setAllData] = useState([]);
  const dispatch = useContext(DispatchContext);
  const [data, isLoaded, error, fetchMore, showLoadMore] = useFetch(
    `/${listingType}/`,
  );

  const [result] = useFetchListAndMerge(data, listingType);

  useEffect(() => {
    result && setAllData(result);
  }, [result]);

  const HandleLoadMore = () => {
    fetchMore();
  };

  return (
    <div key={listingType}>
      <div>Showing Listings for: {listingType}</div>
      <div>
        {allData && (
          <List
            key={listingType}
            data={allData}
            isLoaded={isLoaded}
            error={error}
            listingType={listingType}
            dispatch={dispatch}
          />
        )}
        {isLoaded && showLoadMore ? (
          <button onClick={() => HandleLoadMore()}>Load More!</button>
        ) : (
          isLoaded && <p>No more data...</p>
        )}
      </div>
    </div>
  );
};

export default Listing;

import { useState, useEffect } from 'react';

export const useFetchListAndMerge = (DataObject, ListingType) => {
  const [data, setData] = useState(DataObject);
  const [allData, setAllData] = useState([]);

  useEffect(() => {
    setAllData([]);
  }, [ListingType]);

  useEffect(() => {
    setData(DataObject);
  }, [DataObject, ListingType]);

  useEffect(() => {
    setAllData(allData => [...allData, ...data]);
  }, [data]);

  return [allData];
};

